mapper.select:
select a.*,case when substr(usr_id,0,5) = 'BBMXX' then (select name from bmember where bmember_id = usr_id) when substr(usr_id,0,5) = 'BCRMX' then (select name from createo_mng where createo_mng_id = usr_id) when substr(usr_id,0,5) = 'BCRMX' then (select concat(m.first_name,m.last_name) from member m where member_id = usr_id) end name, case when cr.title is null then (select LISTAGG(case when substr(usr_id,0,5) = 'BBMXX' then (select name from bmember where bmember_id = usr_id) when substr(usr_id,0,5) = 'BCRMX' then (select name from createo_mng where createo_mng_id = usr_id) when substr(usr_id,0,5) = 'BCRMX' then (select concat(m.first_name,m.last_name) from member m where member_id = usr_id) end, ',') from chat_other where chat_room_no = cno and usr_id != #{usr_id}) else cr.title end as title from (select * from (select co2.chat_room_no cno, max(chat_no)a from chat_other co1 right join chat_other co2 on co1.chat_room_no = co2.chat_room_no inner join chat c on co2.chat_other_no = c.chat_other_no where co1.usr_id = #{usr_id} and c.del != 1 GROUP by co2.chat_room_no) last inner join chat c on last.a = c.chat_no inner join chat_other co on co.chat_other_no = c.chat_other_no)a inner join chat_room cr on a.cno = cr.chat_room_no
where a.cno = #{k}
order by sent desc

select usr_id from chat_other co
	where chat_room_no = #{no}
	and usr_id != #{usr_id}
